import { GET_MOVIES, GET_MOVIE_DETAIL, ADD_MOVIE_FAVORITE, REMOVE_MOVIE_FAVORITE } from "../actions";

const initialState = {
    movies: [],
    movieDetail: [],
    favoriteMovies: [],
}

const rootReducer = (state = initialState, {type, payload}) => {
    switch (type) {
        case GET_MOVIES:
            return{
                ...state,
                movies: payload.Search
            }
        case GET_MOVIE_DETAIL:
            return{
                ...state,
                movieDetail: payload
            }
        case ADD_MOVIE_FAVORITE:
            console.log(payload)
            console.log(state.movies[0])
            return{
                ...state,
                favoriteMovies: [...state.favoriteMovies, state.movies.filter(movie => movie.imbdID == payload)]
            }
        case REMOVE_MOVIE_FAVORITE:
            return{
                ...state,
                favoriteMovies: state.favoriteMovies.filter(movie => movie.imdbID !== payload)
            }
        default:
            return state;
    }
}

export default rootReducer;
