import React from 'react'
import { useDispatch } from 'react-redux'
import styles from './index.module.css'
import { addMovieFavorite } from '../../actions'

export default function Card({title, id, type}) {

const dispatch = useDispatch()

  return (
    <div className={styles.divGlobal}>
        <div className={styles.divTitle}>
            <h4>{title}</h4>
        </div>

        {type === 'fav' ? <div className={styles.divButton}>
            <button onClick={() => dispatch(addMovieFavorite(id))}>Fav</button>
        </div>: <div className={styles.divButton}>
            <button onClick={() => dispatch(addMovieFavorite(id))}>X</button>
        </div>}
    </div>
  )
}
